import React, { Component } from 'react'

class UserComponent2 extends Component{
    constructor(users){
        super(users);
        <p>Variables in Class Component.</p>
    }
    render(){
     return (
       users.map((data, indexing)=>
            <div className="row-cols-1 shadow p-3 mb-5 bg-body-tertiary rounded mt-2">
              <div className="col-sm-12 col-md-12 col-lg-12 col-xl-12 col-xxl-12">
              <h1 className="text-decoration-underline">User{indexing+1} Personal Details </h1>
               <h2>
                Name :  {data.name} <br />
                Id : {data.id} <br />
                Email : {data.email} <br />
                <h1 className="text-decoration-underline">Address
                </h1>
                {/* {
                  for (const i of data.address) 
                      <h1>City : data.address[i]</h1>
                    
                }  */}
                
               </h2>
               <p>
                <h1 className="text-decoration-underline">Banking Details
                </h1>
           
                  {
                   data.bank.map((details, index)=>
                   <p> 
                    <h1 className="text-decoration-underline">Bank-{index+1}</h1>  
                    <h2>
                      Account Number : {details.accNo} <br /> Bank Name : {details.bName} <br />
                      Account Type : {details.type}
                    </h2>
                   </p>
                  )
                  }
          
               </p>
              </div>
            </div>
       )
    );
 }
}
export default UserComponent2;
